mapper.select:
SELECT
    concat( concat( 'total', ',' ), sl ) "sl"
FROM
    ( SELECT count( jmid ) sl FROM zhsq_jm

         or SFZH = #{sfzh}
         or SFFZ = #{sffz}
         or SFDJ = #{sfdj}
         or SFKC = #{sfkc}
         or SFXMSF = #{sfxmsf}
         or SFTYJR = #{sftyjr}
         or SFDB = #{sfdb}
         or SFCJ = #{sfcj}
    ) UNION
SELECT
    concat( concat( 'djlr', ',' ), sl ) "sl"
FROM
    ( SELECT count( jmid ) sl FROM zhsq_jm

         and SFDJ = #{sfdj}
    ) UNION
SELECT
    concat( concat( 'kclr', ',' ), sl ) "sl"
    FROM
    ( SELECT count( jmid ) sl FROM zhsq_jm

         and SFKC = #{sfkc}
    ) UNION
SELECT
    concat( concat( 'xmsf', ',' ), sl ) "sl"
    FROM
    ( SELECT count( jmid ) sl FROM zhsq_jm

         and SFXMSF = #{sfxmsf}
    ) UNION
SELECT
    concat( concat( 'tyjr', ',' ), sl ) "sl"
    FROM
    ( SELECT count( jmid ) sl FROM zhsq_jm

         and SFTYJR = #{sftyjr}
    ) UNION
SELECT
    concat( concat( 'dbry', ',' ), sl ) "sl"
    FROM
    ( SELECT count( jmid ) sl FROM zhsq_jm

         and SFDB = #{sfdb}
    ) UNION
SELECT
    concat( concat( 'cjry', ',' ), sl ) "sl"
    FROM
    ( SELECT count( jmid ) sl FROM zhsq_jm

         and SFCJ = #{sfcj}
    )

SELECT nl "keys", to_char(count(*)) "sl" FROM (SELECT case when to_number(nl)  <  20 then '20以下'
when to_number(nl)  >=  20 and to_number(nl)  <=  30 then '20-30'
when to_number(nl) > 30 and to_number(nl)  <=  40 then '31-40'
when to_number(nl) > 40 and to_number(nl)  <=  50 then '41-50'
when to_number(nl) > 50 and to_number(nl)  <=  60 then '51-60'
else '60以上' end AS nl FROM  zhsq_jm

     or SFZH = #{sfzh}
     or SFFZ = #{sffz}
     or SFDJ = #{sfdj}
     or SFKC = #{sfkc}
     or SFXMSF = #{sfxmsf}
     or SFTYJR = #{sftyjr}
     or SFDB = #{sfdb}
     or SFCJ = #{sfcj}
) GROUP BY nl

SELECT
    xb "keys",
    to_char(
    count( * )) "sl"
FROM
    ( SELECT CASE WHEN xb = '男' THEN '男' ELSE '女' END AS xb FROM zhsq_jm

         or SFZH = #{sfzh}
         or SFFZ = #{sffz}
         or SFDJ = #{sfdj}
         or SFKC = #{sfkc}
         or SFXMSF = #{sfxmsf}
         or SFTYJR = #{sftyjr}
         or SFDB = #{sfdb}
         or SFCJ = #{sfcj}
    )
GROUP BY
    xb

SELECT to_char(count( * )) "sl", mz "keys" FROM (
    SELECT
        CASE WHEN mz = '汉族' THEN '汉族'
             WHEN mz = '回族' THEN '回族'
             WHEN mz = '苗族' THEN '苗族'
             WHEN mz = '土家族' THEN '土家族'
             WHEN mz = '藏族' THEN '藏族' ELSE '其他' END AS mz
    FROM zhsq_jm

         or SFZH = #{sfzh}
         or SFFZ = #{sffz}
         or SFDJ = #{sfdj}
         or SFKC = #{sfkc}
         or SFXMSF = #{sfxmsf}
         or SFTYJR = #{sftyjr}
         or SFDB = #{sfdb}
         or SFCJ = #{sfcj}
     ) GROUP BY mz

        SELECT a.sl "sl", b.ldmc "keys" FROM (
	        SELECT to_char(count( * )) sl, ldid FROM zhsq_jm

                 or SFZH = #{sfzh}
                 or SFFZ = #{sffz}
                 or SFDJ = #{sfdj}
                 or SFKC = #{sfkc}
                 or SFXMSF = #{sfxmsf}
                 or SFTYJR = #{sftyjr}
                 or SFDB = #{sfdb}
                 or SFCJ = #{sfcj}

	        GROUP BY ldid ) a INNER JOIN zhsq_ld b ON a.ldid = b.ldid
