mapper.select:
select count(1) as mun, count(case when jc.SFDJ='是' then 1 else null end) as djlr, count(case when jc.SFKC='是' then 1 else null end) as kclr, count(case when jc.SFXMSF='是' then 1 else null end) as xmsf, count(case when jc.SFTYJR='是' then 1 else null end) as twjr, count(case when jc.SFDB='是' then 1 else null end) as dbry, count(case when jc.SFCJ='是' then 1 else null end) as cjlr FROM (SELECT * FROM ZHSQ_JM where (SFDJ like '%是%' or SFKC like '%是%' or SFXMSF like '%是%' or SFTYJR like '%是%' or SFDB like '%是%' or SFCJ like '%是%')
and XQID = #{xqid} and SQID = #{sqid}
) jc

select LDMC as "name",MUN as "value" from (
SELECT
LDMC,
count(LDID) as MUN
FROM
(SELECT ld.LDID,ld.LDMC FROM ZHSQ_JM jm join ZHSQ_LD ld on jm.LDID=ld.LDID
where (jm.SFDJ = '是' OR jm.SFKC = '是' OR jm.SFXMSF = '是' OR jm.SFTYJR = '是' OR jm.SFDB = '是' OR jm.SFCJ = '是')
 and ld.XQID = #{xqid}
 and ld.SQID = #{sqid}
 and jm.SFDJ = #{sfdj}
 and jm.SFKC = #{sfkc}
 and jm.SFXMSF = #{sfxmsf}
 and jm.SFTYJR = #{sftyjr}
 and jm.SFDB = #{sfdb}
 and jm.SFCJ = #{sfcj}
)  a
 GROUP BY LDMC ORDER BY MUN DESC ) m where rownum  <=  5

 SELECT
 count(case when jc.NL  <  '20' then 1 else null end) as a,
 count(case when jc.NL  >=  '20' and jc.NL  <=  '30' then 1 else null end) as b,
 count(case when jc.NL  >=  '31' and jc.NL  <=  '40' then 1 else null end) as c,
 count(case when jc.NL  >=  '41' and jc.NL  <=  '50' then 1 else null end) as d,
 count(case when jc.NL  >=  '51' and jc.NL  <=  '60' then 1 else null end) as e,
 count(case when jc.NL  >  '60' then 1 else null end) as f
 FROM
 (SELECT * FROM ZHSQ_JM
 where (SFDJ = '是' OR SFKC = '是' OR SFXMSF = '是' OR SFTYJR = '是' OR SFDB = '是' OR SFCJ = '是')

      and XQID = #{xqid}
      and SQID = #{sqid}
      and SFDJ = #{sfdj}
      and SFKC = #{sfkc}
      and SFXMSF = #{sfxmsf}
      and SFTYJR = #{sftyjr}
      and SFDB = #{sfdb}
      and SFCJ = #{sfcj}

)  jc

SELECT XB as "name",count(1) as "value" FROM ZHSQ_JM
where (SFDJ = '是' OR SFKC = '是' OR SFXMSF = '是' OR SFTYJR = '是' OR SFDB = '是' OR SFCJ = '是')

     and XQID = #{xqid}
     and SQID = #{sqid}
     and SFDJ = #{sfdj}
     and SFKC = #{sfkc}
     and SFXMSF = #{sfxmsf}
     and SFTYJR = #{sftyjr}
     and SFDB = #{sfdb}
     and SFCJ = #{sfcj}
GROUP BY XB

SELECT MZ as "name",count(1) as "value" FROM ZHSQ_JM
where (SFDJ = '是' OR SFKC = '是' OR SFXMSF = '是' OR SFTYJR = '是' OR SFDB = '是' OR SFCJ = '是')
     and XQID = #{xqid}
     and SQID = #{sqid}
     and SFDJ = #{sfdj}
     and SFKC = #{sfkc}
     and SFXMSF = #{sfxmsf}
     and SFTYJR = #{sftyjr}
     and SFDB = #{sfdb}
     and SFCJ = #{sfcj}
 GROUP BY MZ

SELECT JMID as "jmid",XM as "xm", XB as "xb",DH as "dh",NL as "nl",JG as "jg",HJXZ as "hjxz",MZ as "mz",ZZMM　as "zzmm",XL as "xl",SFZH as "sfzh",SFFZ as "sffz",FWID as "fwid",LDID as "ldid",ZZ as "zz",SFDJ as "sfdj",SFKC as "sfkc",SFXMSF as "sfxmsf",SFTYJR as "sftyjr",SFDB as "sfdb",SFCJ as "sfcj",SFCZ as "sfcz" FROM ZHSQ_JM

     and XQID = #{xqid}
     and SQID = #{sqid}
     and SFDJ = #{sfdj}
     and SFKC = #{sfkc}
     and SFXMSF = #{sfxmsf}
     and SFTYJR = #{sftyjr}
     and SFDB = #{sfdb}
     and SFCJ = #{sfcj}



        and XM like #{_zdry}

    and (SFDJ = '是' OR SFKC = '是' OR SFXMSF = '是' OR SFTYJR = '是' OR SFDB = '是' OR SFCJ = '是')

SELECT count(1) FROM (
SELECT JMID as "jmid",XM as "xm", XB as "xb",DH as "dh",NL as "nl",JG as "jg",HJXZ as "hjxz",MZ as "mz",ZZMM　as "zzmm",XL as "xl",SFZH as "sfzh",SFFZ as "sffz",FWID as "fwid",LDID as "ldid",ZZ as "zz",SFDJ as "sfdj",SFKC as "sfkc",SFXMSF as "sfxmsf",SFTYJR as "sftyjr",SFDB as "sfdb",SFCJ as "sfcj",SFCZ as "sfcz" FROM ZHSQ_JM

     and XQID = #{xqid}
     and SQID = #{sqid}
     and SFDJ = #{sfdj}
     and SFKC = #{sfkc}
     and SFXMSF = #{sfxmsf}
     and SFTYJR = #{sftyjr}
     and SFDB = #{sfdb}
     and SFCJ = #{sfcj}



        and XM like #{_zdry}

    and (SFDJ = '是' OR SFKC = '是' OR SFXMSF = '是' OR SFTYJR = '是' OR SFDB = '是' OR SFCJ = '是')
)

SELECT
count(1) as rwzs,
count(case when jc.XCZT='已巡' then 1 else null end) as yx,
count(case when jc.XCZT='在巡' then 1 else null end) as zx,
count(case when jc.XCZT='未巡' then 1 else null end) as wx
FROM
(SELECT * FROM [dbo].[ZHSQ_WYXC]

     and XQID = #{xqid}
     and SQID = #{sqid}
) as jc

SELECT XCZT ,count(1) as MUN FROM [dbo].[ZHSQ_WYXC] xc left join [dbo].[ZHSQ_WYRY] ry on xc.WYRYID=ry.WYRYID JOIN [dbo].[ZHSQ_XQ] xq on ry.XQID=xq.XQID

     and xq.XQID = #{xqid}
     and xq.SQID = #{sqid}

GROUP BY XCZT

SELECT *  FROM [dbo].[ZHSQ_WYXC]

     and XQID = #{xqid}
     and SQID = #{sqid}
     and XCZT = #{xczt}


        and (RWMC like #{_xjrw} or XM like #{_xjrw} or XCZT like #{_xjrw})

        SELECT
				(CASE WHEN t1.zs = 0 THEN 0 ELSE CAST(round((CAST(round(t2.zasjs,2) as DECIMAL(18,2)) /CAST(round(t1.zs,2) as DECIMAL(18,2)))*100,2) as DECIMAL(18,2)) END) as zasjL,
				(CASE WHEN t1.zs = 0 THEN 0 ELSE CAST(round((CAST(round(t3.wssjs,2) as DECIMAL(18,2)) /CAST(round(t1.zs,2) as DECIMAL(18,2)))*100,2) as DECIMAL(18,2)) END) as wssjL,
				(CASE WHEN t1.zs = 0 THEN 0 ELSE CAST(round((CAST(round(t4.nysjs,2) as DECIMAL(18,2)) /CAST(round(t1.zs,2) as DECIMAL(18,2)))*100,2) as DECIMAL(18,2)) END) as nysjL,
				(CASE WHEN t1.zs = 0 THEN 0 ELSE CAST(round((CAST(round(t5.mdjfs,2) as DECIMAL(18,2)) /CAST(round(t1.zs,2) as DECIMAL(18,2)))*100,2) as DECIMAL(18,2)) END) as mdjfL,
				(CASE WHEN t1.zs = 0 THEN 0 ELSE CAST(round((CAST(round(t6.bxsjs,2) as DECIMAL(18,2)) /CAST(round(t1.zs,2) as DECIMAL(18,2)))*100,2) as DECIMAL(18,2)) END) as bxsjL
				FROM
				(
            SELECT ISNULL(COUNT(1), 0) as zs FROM ZHSQ_YC

             and XQID = #{xqid}
             and SQID = #{sqid}

        ) as t1
        OUTER apply(
            SELECT ISNULL(COUNT(1), 0) as zasjs FROM ZHSQ_YC

             and XQID = #{xqid}
             and SQID = #{sqid}
         and SJLX = '治安事件'
        ) as t2
				OUTER apply(
            SELECT ISNULL(COUNT(1), 0) as wssjs FROM ZHSQ_YC

             and XQID = #{xqid}
             and SQID = #{sqid}
         and SJLX = '卫生事件'
        ) as t3
				OUTER apply(
            SELECT ISNULL(COUNT(1), 0) as nysjs FROM ZHSQ_YC

             and XQID = #{xqid}
             and SQID = #{sqid}
         and SJLX = '能源事件'
        ) as t4
				OUTER apply(
            SELECT ISNULL(COUNT(1), 0) as mdjfs FROM ZHSQ_YC

             and XQID = #{xqid}
             and SQID = #{sqid}
         and SJLX = '矛盾纠纷'
        ) as t5
				OUTER apply(
            SELECT ISNULL(COUNT(1), 0) as bxsjs FROM ZHSQ_YC

             and XQID = #{xqid}
             and SQID = #{sqid}
         and SJLX = '报修事件'
        ) as t6

SELECT SJLX ,count(1) as MUN FROM ZHSQ_YC

     and XQID = #{xqid}
     and SQID = #{sqid}

GROUP BY SJLX

SELECT T.YCID AS YCID,T.YCSJ AS YCSJ,T.YCNR AS YCNR,T.YCJB AS YCJB,T.CZZT AS CZZT,T.YCLY AS YCLY,T.SJLX AS SJLX,T.GJSJ_ID AS GJSJID,T.EVENT_TYPE AS EVENT_TYPE FROM(
SELECT YCID,YCSJ,YCNR,YCJB,CZZT,YCLY,SJLX,GJSJ_ID,EVENT_TYPE,rownum as no FROM ZHSQ_YC

    and XQID = #{xqid}
    and SQID = #{sqid}
    and CZZT = #{czzt}


        and (SJLX like #{_sjlx} OR YCNR like #{_sjlx} OR CZZT like #{_sjlx})


order by CZSJ) T
where no between (#{page}-1)*#{size}+1 and #{page}*#{size}

SELECT count(1) FROM (
SELECT YCID,YCSJ,YCNR,YCJB,CZZT,YCLY,SJLX,GJSJ_ID,EVENT_TYPE,rownum as no FROM ZHSQ_YC

    and XQID = #{xqid}
    and SQID = #{sqid}
    and CZZT = #{czzt}


        and (SJLX like #{_sjlx} OR YCNR like #{_sjlx} OR CZZT like #{_sjlx})

)
